def makeRooms()
  numberOfRooms = rand(10..20)
  count = 0
  while count < numberOfRooms
    height = rand(5..20)
    width = rand(10..30)
    y = rand(0...$map.length - height)
    x = rand(0...$map[0].length - width)
    if validateRoom(y,x,height,width) == true
      $rooms.push({:x => x, :y => y, :height => height, :width => width})
      for row in (y..y+height)
        for column in (x..x+width)
          $map[row][column]=0
        end
      end
      count += 1
    end
  end
end
def validateRoom(y,x,height,width)
  for row in (y..y+height)
    for column in (x..x+width)    
      if $map[row][column] != " "     
        return false
      end
    end
  end
  return true
end

def makePaths()
  for room in $rooms
    x = room[:x]
    xMax = room[:x] + room[:width]
    y = room[:y]
    yMax = room[:y] + room[:height]
    closestYDist = 100
    closestRoomY = nil
    yPathX = nil
    closestXDist = 100
    closestRoomX = nil
    xPathY = nil
    count = 0
    for otherRoom in $rooms
      count += 1
      if otherRoom == room
        next 
      end
      otherX = otherRoom[:x]
      otherXMax = otherRoom[:x] + room[:width]
      otherY = otherRoom[:y]
      otherYMax = otherRoom[:y] + room[:height]

      #if within x
      for checkX in (x..xMax)
        if checkX < otherXMax && checkX > otherX
          #find closest y
          if closestRoomY == nil
            closestYDist = (y - (otherY + otherYMax)).abs
            closestRoomY = otherRoom
            yPathX = checkX
          elsif (y-(otherY+otherYMax)).abs < closestYDist && checkX < otherXMax && checkX > otherX
            closestYDist = (y - (otherY + otherYMax)).abs
            closestRoomY = otherRoom
            yPathX = checkX
          end
            
        end
      end
      for checkY in (y..yMax)
        if checkY < otherYMax && checkY > otherY
          #find closest y
          if closestRoomX == nil
            closestXDist = (x - (otherX + otherXMax)).abs
            closestRoomX = otherRoom
            xPathY = checkY
          elsif (x-(otherX+otherXMax)).abs < closestXDist && checkY < otherYMax && checkY > otherY
            closestXDist = (x - (otherX + otherXMax)).abs
            closestRoomX = otherRoom
            xPathY = checkY
          end
        end
      end
    end 
    if closestRoomY != nil
      $paths.push({:x => yPathX, :xMax => yPathX, :y =>room[:y], :yMax =>closestRoomY[:y] + closestRoomY[:height]})
    end
    if closestRoomX != nil
      $paths.push({:x => room[:x], :xMax => closestRoomX[:x] + closestRoomX[:width], :y => xPathY, :yMax => xPathY})
    end
  end
end
def drawPaths()
  for path in $paths
    y = path[:y]
    yMax = path[:yMax]
    x = path[:x]
    xMax = path[:xMax]
    for row in (y..yMax)
      for column in (x..xMax)
        $map[row][column]=0
      end
    end
  end
  for path in $paths
    y = path[:yMax]
    yMax = path[:y]
    x = path[:xMax]
    xMax = path[:x]
    for row in (y..yMax)
      for column in (x..xMax)
        $map[row][column]=0
      end
    end
  end
end
def drawWalls()
  rowCount = 0
  for row in $map
    colCount = 0
    for col in row
      if $map[rowCount][colCount] == 0
        if $map[rowCount+1][colCount] == ' '
          $map[rowCount+1][colCount] = '_'
        end
        if $map[rowCount-1][colCount] == ' '
          $map[rowCount-1][colCount] = '_'
        end
        if $map[rowCount][colCount+1] == ' '
          $map[rowCount][colCount+1] = '|'
        end
        if $map[rowCount][colCount-1] == ' '
          $map[rowCount][colCount-1] = '|'
        end
      end
      colCount += 1
    end
    rowCount +=1
  end
end
