 def move(input)
  case input
  when "\e[A"
    playerCoords = [playerCoords[0]-1, playerCoords[1]]
    $map[playerCoords[0][playerCoords[1]] = '@'
  when "\e[B"
    playerCoords = [playerCoords[0]+1, playerCoords[1]]
    $map[playerCoords[0][playerCoords[1]] = '@'
  when "\e[C"
    playerCoords = [playerCoords[0], playerCoords[1]+1]
    $map[playerCoords[0][playerCoords[1]] = '@'
  when "\e[D"
    playerCoords = [playerCoords[0], playerCoords[1]-1]
    $map[playerCoords[0][playerCoords[1]] = '@'
 end
def read_char
  STDIN.echo = false
  STDIN.raw!

  input = STDIN.getc.chr
  if input == "\e" then
    input << STDIN.read_nonblock(3) rescue nil
    input << STDIN.read_nonblock(2) rescue nil
  end
ensure
  STDIN.echo = true
  STDIN.cooked!

  return input
end

# oringal case statement from:
# http://www.alecjacobson.com/weblog/?p=75
def show_single_key
  c = read_char

  case c
  when " "
    puts "SPACE"
  when "\t"
    puts "TAB"
  when "\r"
    puts "RETURN"
  when "\n"
    puts "LINE FEED"
  when "\e"
    puts "ESCAPE"
  when "\e[A"
    move(c)
  when "\e[B"
    move(c)
  when "\e[C"
    move(c)
  when "\e[D"
    move(c)
  when "\177"
    puts "BACKSPACE"
  when "\004"
    puts "DELETE"
  when "\e[3~"
    puts "ALTERNATE DELETE"
  when "\u0003"
    puts "CONTROL-C"
    exit 0
  when /^.$/
    puts "SINGLE CHAR HIT: #{c.inspect}"
  else
    puts "SOMETHING ELSE: #{c.inspect}"
  end
end

show_single_key while(true)